import { Component, Input, Output, OnInit, EventEmitter } from '@angular/core';
import { Project } from '../project/project';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.css']
})
export class FilterComponent {
  @Input() projects: Project[] = [];
  @Input() filters: string[] = [""];
  @Output() filter_item = new EventEmitter<string>();
  item: string = "";
  platform_filters: string[] = [];
  language_filters: string[] = [];
  skill_filters: string[] = [];

  constructor() {}

  ngOnInit() {
    this.setFilters();
  }

  setFilters() {
    for(let i = 0; i < this.filters.length; i++){
      let filter_item = this.filters[i].split("_");

      switch (filter_item[0]){
        case "platform": {
          this.platform_filters.push(this.filters[i]);
          break;
        }
        case "language": {
          this.language_filters.push(this.filters[i]);
          break;
        }
        case "skill": {
          this.skill_filters.push(this.filters[i]);
          break;
        }
        default: {
          break;
        }
      }
    }

  }

  getItem(item: string) {
    this.item = item;
    this.emitEvent(item);
    this.toggleActiveBackground(item);
  }

  private emitEvent(item: string) {
    switch (item) {
      case "C#": {
        this.filter_item.emit("cs");
        break;
      }
      case "Project Management": {
        this.filter_item.emit("pm");
        break;
      }
      case "HTML/CSS": {
        this.filter_item.emit("htmlcss");
        break;
      }
      case "JavaScript": {
        this.filter_item.emit("js");
        break;
      }
      default: {
        this.filter_item.emit(item.toLowerCase());
        break;
      }
    }
  }

  private toggleActiveBackground(item: string) {
    var element = document.getElementById(item)!;
    element.classList.toggle("selected-filter");
  }
}
<div class="container d-flex justify-content-center">
    <div class="btn-group btn-group-sm" role="group" aria-label="Basic example">
        <div class="dropdown">
            <button type="button" class="btn btn-outline-muted dropdown-toggle" data-bs-toggle="dropdown" data-bs-auto-close="outside" data-bs-display="static" aria-expanded="false">
                Filter
            </button>
            <ul class="dropdown-menu w-100">
              <li (click)="getItem(item)" *ngFor="let item of filters"><button [id]="item" class="dropdown-item" type="button">{{item}}</button></li>
            </ul>
        </div>
        <div class="dropdown">
            <button type="button" class="btn btn-outline-muted dropdown-toggle" data-bs-toggle="dropdown" data-bs-auto-close="outside" data-bs-display="static" aria-expanded="false">
                Language
            </button>
            <ul class="dropdown-menu w-100">
                <li (click)="getItem(item)" *ngFor="let item of language_filters"><button [id]="item" class="dropdown-item" type="button">{{item}}</button></li>
            </ul>
        </div>

        <div class="dropdown">
            <button type="button" class="btn btn-outline-muted dropdown-toggle" data-bs-toggle="dropdown" data-bs-auto-close="outside" data-bs-display="static" aria-expanded="false">
                TBA
            </button>
            <ul class="dropdown-menu dropdown-menu-lg-end w-100">
              <li><button class="dropdown-item" type="button">TBA</button></li>
              <li><button class="dropdown-item" type="button">TBA</button></li>
            </ul>
        </div>
    </div>
</div>
