import { Component } from '@angular/core';
import { FEATURED_PROJECTS } from '../projects';

@Component({
  selector: 'app-featured-projects',
  templateUrl: './featured-projects.component.html',
  styleUrls: ['./featured-projects.component.css']
})
export class FeaturedProjectsComponent {
  projects = FEATURED_PROJECTS;
  buttons = ["ANGULAR", "DESIGN","TYPESCRIPT","BOOTSTRAP"]
}
<div class="row p-0 mx-0" *ngFor="let project of projects">
  <div class="col-12 col-md-6">
    <img [src]="project.img" class="w-100">
  </div>
  <div class="col-12 col-md-6 m-0 p-0 text-black">
    <h3 class="my-3 fs-5 fw-bold text-center">{{project.title | uppercase}}</h3>
    <ul class="d-flex flex-wrap align-items-center justify-content-center p-0">
      <li class="btn btn-white me-2 p-2 fs-6 fw-bold" *ngFor="let item of buttons">{{item}}</li>
    </ul>    
  </div>
</div>
