import { Component, Input, Output, OnInit, EventEmitter } from '@angular/core';
import { Project } from '../project/project';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.css']
})
export class FilterComponent {
  @Input() projects: Project[] = [];
  @Input() filters: string[] = [""];
  @Output() filter_item = new EventEmitter<string>();
  item: string = "";
  platform_filters: string[] = [];
  language_filters: string[] = [];
  skill_filters: string[] = [];

  constructor() {}

  ngOnInit() {
    this.setFilters();
  }

  setFilters() {
    for(let i = 0; i < this.filters.length; i++){
      let filter_item = this.filters[i].split("_");

      switch (filter_item[0]){
        case "platform": {
          this.platform_filters.push(this.filters[i]);
          break;
        }
        case "language": {
          this.language_filters.push(this.filters[i]);
          break;
        }
        case "skill": {
          this.skill_filters.push(this.filters[i]);
          break;
        }
        default: {
          break;
        }
      }
    }

  }

  getItem(item: string) {
    this.item = item;
    this.emitEvent(item);
    this.toggleActiveBackground(item);
  }

  private emitEvent(item: string) {
    switch (item) {
      case "C#": {
        this.filter_item.emit("cs");
        break;
      }
      case "Project Management": {
        this.filter_item.emit("pm");
        break;
      }
      case "HTML/CSS": {
        this.filter_item.emit("htmlcss");
        break;
      }
      case "JavaScript": {
        this.filter_item.emit("js");
        break;
      }
      default: {
        this.filter_item.emit(item.toLowerCase());
        break;
      }
    }
  }

  private toggleActiveBackground(item: string) {
    var element = document.getElementById(item)!;
    element.classList.toggle("selected-filter");
  }
}
<div class="container m-0 p-0">
    <div class="btn-group btn-group-sm d-flex justify-content-center" role="group" aria-label="Dropdown Filter Buttons">
        <div class="dropdown">
            <button type="button" class="dropdown-toggle start-button py-3 pe-2 m-0" data-bs-toggle="dropdown" data-bs-auto-close="outside" aria-expanded="false">
                Platform
                <i class="fa-sharp fa-solid fa-caret-down mx-1 fa-2xs"></i>
            </button>
            <ul class="dropdown-menu">
                <li (click)="getItem(item)" *ngFor="let item of platform_filters"><button [id]="item" class="dropdown-item p-3" type="button">{{item}}</button></li>
            </ul>
        </div>
        <div class="dropdown">
            <button type="button" class="btn dropdown-toggle center-button py-3 px-2 m-0" data-bs-toggle="dropdown" data-bs-auto-close="outside" aria-expanded="false">
                Language
                <i class="fa-sharp fa-solid fa-caret-down  mx-1 fa-2xs"></i>
            </button>
            <ul class="dropdown-menu">
                <li (click)="getItem(item)" *ngFor="let item of language_filters"><button [id]="item" class="dropdown-item p-3" type="button">{{item}}</button></li>
            </ul>
        </div>
        <div class="dropdown">
            <button type="button" class="btn dropdown-toggle end-button py-3 ps-2 m-0" data-bs-toggle="dropdown" data-bs-auto-close="outside" aria-expanded="false">
                Skill
                <i class="fa-sharp fa-solid fa-caret-down  mx-1 fa-2xs"></i>
            </button>
            <ul class="dropdown-menu">
                <li (click)="getItem(item)" *ngFor="let item of skill_filters"><button [id]="item" class="dropdown-item p-3" type="button">{{item}}</button></li>
            </ul>
        </div>
    </div>
</div>
