import { Component, OnInit,  } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  introduction: string = "";


  constructor(){
    this.typeWriter("> IS ANYONE THERE?", 50);
  }

  ngOnInit(){

  }

  ngAfterViewInit() {
    this.typeWriter("> IS ANYONE THERE?", 50);

  }

 typeWriter(string: string, typingSpeed: number) {
    if (document.getElementById("typewriter")) {
      for (let i = 0; i < string.length; i++) {
        document.getElementById("typewriter")!.innerHTML += string.charAt(i);
        setTimeout(this.typeWriter, typingSpeed);
      }
    }
 }
}
<div class="container-body px-0 mx-0">
    <app-navbar></app-navbar>

    <header id="header" class="header bg-black pb-5">
        <div class="circle"></div>
        <div class="container">
            <div id="title" class="d-flex justify-content-center text-center bg-transparent text-white">
                <h1 id="typewriter" class="fs-5 fw-bold">{{introduction}}</h1>
            </div>
            <div class="row bg-transparent text-white pb-3">
                <div class="col">
                    <h2 class="fs-5 fw-bold">SOFTWARE ENGINEER</h2>
                    <p>
                        As a developer with a keen eye for design and problem-solving, 
                        I’ve helped large organizations and rising startups successfully 
                        reach their full potential by satisfying clients 
                        and bringing products to life.
                    </p>

                    <p><a routerLink="/portfolio"
                        routerLinkActive="active">VIEW PROJECTS</a> or <a href="...">READ ABOUT ME</a>
                    </p>
                </div>
                <div class="col col-12 col-md-6">
                </div>
            </div>
        </div>
    </header>

    <app-featured-projects></app-featured-projects>
    <app-footer></app-footer>
</div>
