import { Component, OnInit, ChangeDetectorRef, ElementRef, HostListener } from '@angular/core';
import { Router } from "@angular/router";
import {
  trigger,
  state,
  style,
  animate,
  keyframes,
  transition
} from '@angular/animations';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  animations: [
    trigger('starsAnim', [
      transition('down => up', [
        animate("100s linear", keyframes([
          style({ transform: 'translateY(0vh)' }),
          style({ transform: 'translateY(-200vh)' })
        ]))
      ])
    ]),
    trigger('starsAnim1', [
      transition('down => up', [
        animate("125s linear", keyframes([
          style({ transform: 'translateY(0vh)' }),
          style({ transform: 'translateY(-200vh)' })
        ]))
      ])
    ]),
    trigger('starsAnim2', [
      transition('down => up', [
        animate("150s linear", keyframes([
          style({ transform: 'translateY(0vh)' }),
          style({ transform: 'translateY(-200vh)' })
        ]))
      ]),
    ])
  ]
})

export class HomeComponent {
  constructor(private router: Router, private cdr: ChangeDetectorRef) { }
  private stars: string = "";
  public state = 'down';
  public reach_the_stars: number = 0;
  public year = (new Date()).getFullYear();
  private colors: Array<string> = ["#3C4F6C75", "#1A4C4A75", "#FED3B7", "#FF925E80", "#FF7A66", "#ABA3D275", "#FFD9EE50", "#FFD96C", "#D65F9150"]


  ngOnInit() {
    this.stars = this.box_shadow(400, 250);
    document.getElementById("stars")!.style.boxShadow = this.stars;

  }

  ngAfterViewInit() {
    if (this.state === "down") { this.state = "up" }
    else { this.state = "down" }
    this.cdr.detectChanges();
  }

  onEnd(event: any) {
    if (this.state === "up") { this.state = "down" }
    else { this.state = "up" }
    // console.log("animation ended: state = ", this.state);
  }

  private rng(min: number, max: number) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  private box_shadow(num_stars: number, vh_height: number) {
    let box_shadow_str = "";

    for (let i = 0; i <= num_stars; i++) {
      box_shadow_str += this.rng(0, 100).toString() + "vw " + this.rng(0, vh_height).toString() + "vh " + "#faf9f7"

      if (i != num_stars) {
        box_shadow_str += ","
      }

    }

    return box_shadow_str
  }

  public add_trip() {
    this.reach_the_stars += 1;
  }
}
<div class="container-body px-0 mx-0">
    <app-navbar></app-navbar>

    <header id="header" class="header bg-black pb-5">
        <div class="container">
            <div class="stars-position" [@starsAnim]="this.state" (@starsAnim.done)="onEnd($event)">
                <div id="stars" alt=""></div>
              </div>
            <div id="title" class="row d-flex justify-content-center text-center bg-transparent text-white">
                <h1 class="fs-4 fw-bold">JASMINE IRADA</h1>
            </div>

            <div class="row bg-transparent text-white">
                <div class="col">
                    <h2 class="fs-5 fw-bold">SOFTWARE ENGINEER</h2>
                    <p>
                        As a developer with a keen eye for design and problem-solving, 
                        I’ve helped large organizations and rising startups successfully 
                        reach their full potential by satisfying clients 
                        and bringing products to life.
                    </p>

                    <p><a routerLink="/portfolio"
                        routerLinkActive="active">VIEW PROJECTS</a> or <a href="...">READ ABOUT ME</a>
                    </p>
                </div>
                <div class="col col-12 col-md-6">
                </div>
            </div>
        </div>
    </header>

    <app-featured-projects></app-featured-projects>
    <app-footer></app-footer>
</div>
