import { Project } from './project';
import { PROJECTS } from './projects';

export const PROJECT_SKILLS: string[] =  ["platform_windows","platform_macos","platform_ios","platform_android","language_htmlcss",
"language_typescript","skill_angular","skill_bootstrap","skill_design"];

function getProjectSkills() {
    let project_skills: Map<string, number[]> = new Map();

    for (let i = 0; i < PROJECTS.length; i++){
        let skills = PROJECTS[i].skills;
        let id = PROJECTS[i].id;

        for (let j = 0; j < PROJECTS[i].skills.length; j++){
            let key: string = skills[j];
            if (project_skills.has(key)) {
                let values: number[] | undefined = project_skills.get(key);
                if (values?.indexOf(id) != undefined) {
                    values = values.concat([id]);
                    project_skills.set(key, values);
                }
            } else {
                project_skills.set(skills[j], [id]);
            }
        }
    }

    return project_skills;
}
