import { Component, HostListener } from '@angular/core';
import { Project } from '../project/project';
import { ProjectService } from '../project/project.service';
import { PROJECT_SKILLS, project_skills } from '../project/project-skills.function';

@Component({
  selector: 'app-portfolio',
  templateUrl: './portfolio.component.html',
  styleUrls: ['./portfolio.component.css']
})
export class PortfolioComponent {
  projects: Project[] = [];
  readonly filters: string[] = PROJECT_SKILLS;
  readonly project_skills: Map<string, number[]> = project_skills;

  filtered_item: string = "";
  filtered_projects: Project[] = [];
  selected_filters: string[] = [];


  constructor(private projectService: ProjectService) {}

  ngOnInit(): void {
    this.getProjects();
    this.filtered_projects = this.projects;
    this.getProjectSkillsMap();
  }

  @HostListener('document:click', ['$event'])
    documentClick(event: MouseEvent) {
        // this.filtered_projects.pop();
  }

  private getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  private getProjectSkillsMap(): void {
    console.log(project_skills);
    console.log(this.filters);
  }


  filter($event: string) {
    this.filtered_item = $event; //event emitted by filters component
    this.updateFilters();
    this.updateFilteredProjects();
  }

  private updateFilters(): void {
    if (this.selected_filters.includes(this.filtered_item)) {
      let index = this.selected_filters.indexOf(this.filtered_item);
      this.selected_filters.splice(index, 1);
    } else {
      this.selected_filters.push(this.filtered_item);
    }

    console.log("selected_filters: " + this.selected_filters);
  }

  private getVisibleProjects(): Set<number> {
    let visible_projects: number[] = [];
    for (let i = 0; i < this.selected_filters.length; i++){
      let projects = this.project_skills.get(this.selected_filters[i])!;
      visible_projects = visible_projects.concat(projects);
    }

    return new Set(visible_projects);
  }

  private updateFilteredProjects(): void {
    this.filtered_projects = [];
    let projects: Set<number> = this.getVisibleProjects();

    for (let i = 0; i < this.projects.length; i++){
      if (projects.has(this.projects[i].id)){
        this.filtered_projects.push(this.projects[i]);
      }
    }
  }



}
