import { Component } from '@angular/core';
import { PROJECTS } from '../projects';

@Component({
  selector: 'app-project-card',
  templateUrl: './project-card.component.html',
  styleUrls: ['./project-card.component.css']
})
export class ProjectCardComponent {
  projects = PROJECTS;
  buttons = ["ANGULAR", "BOOTSTRAP","TYPESCRIPT","DESIGN"];
}
<div class="row row-cols-1 row-cols-md-3">
    <div class="col" *ngFor="let project of projects">
        <div class="card">
            <img [src]="project.img" class="card-img-top" alt="...">
            <div class="card-body">
                <ul class="d-flex flex-wrap align-items-center justify-content-center p-0">
                    <li class="badge bg-dark me-2 my-1 p-2 fw-bold" *ngFor="let item of buttons">{{item}}</li>
                </ul>
            </div>
        </div>  
    </div>
</div>
